let res = pm.response.json();
if (res["Code"] != 200) {
    return;
}
let payUrl = res["Data"]["pay_url"];
console.log(payUrl);

let apiUrl = "https://api.caoliao.net/api/qrcode/code?text=" + payUrl;
pm.sendRequest(apiUrl, function (err, response) {
    const html = response.stream.toString();
    // qr.api.cli.im/newqr/create?data=123&kid=cliim&key=4b243134fc1d98a864b053ae488211c6
    const regex = /src=\"(\/\/qr\.api\.cli\.im\/newqr\/create\?data=.*?&kid=cliim&key=.*?)\"/;
    const match = html.match(regex);
    if (!match) {
        return;
    }
    console.log(match);
    const qrUrl = "https:" + match[1];
    console.log(qrUrl);

    const template = `<html><img height="200px" width="200px" src="{{imgTemplate}}" /></html>`;
    pm.visualizer.set(template, {
        imgTemplate: qrUrl,
    });
});

